import { transactionTypes } from "./types";
import operations from "./operations";
import { message } from "antd";

export const transactDetails = {
  loadTransactionDetails
}
function loadTransactionDetails() {
    return async dispatch => {
      dispatch(request());
      try {
        const response = await operations.loadTransactionDetails();
        dispatch(success(response));
      } catch (error) {
        dispatch(failure(error));
        message.error("Fail to load list of transactions");
      }
    };
  
    function request() {
      return { type: transactionTypes.LOAD_TRANSACTION_DETAILS_REQUEST};
    }
  
    function success(response) {
      return { type: transactionTypes.LOAD_TRANSACTION_DETAILS_SUCCESS, response };
    }
  
    function failure(error) {
      return { type: transactionTypes.LOAD_TRANSACTION_DETAILS_FAILURE, error };
    }
  }
