import React, { Component } from "react";
import { Table } from "antd";
import { Route, Switch, Router, Link } from "react-router-dom";
import transactionDetailsService from "./TransactionDetailsService";
import { createBrowserHistory } from "history";
const history = createBrowserHistory();
/*export const ChildTransactionDetailsPage = () => {
    console.log('ChildTransactionDetailsPage');
    return ( 
    <div>     
    
    <p>test</p>

    </div>
    );
  };*/

  function Users() {
    return <h2>Users</h2>;
  }

export class TransactionDetails extends Component {
    constructor(props) {
      super(props);
      this.state = {
        loading: false,
        data: [],
      };
    }

    componentDidMount() {
        this.loadData();
    }

    loadData(){
        this.setState({ loading: true }, () => {
            transactionDetailsService
              .loadTransactionDetails()
              .then(data => {
                this.setState({ data });
                console.log(data);
              })
              .catch(err => {
                console.log("DEBUG err ", err);
              })
              .then(() => {
                this.setState({ loading: false });
              });
          });
    }

    handleAction = record => {
        let viewText = record;
        return (
            //<Route key="/child-details" path="/child-details" component={ChildTransactionDetailsPage}>
                
            //<Link to="/child-details">
            //    <span>{viewText}</span>
            //</Link>
           //</Route>
           <Router history={history}>
            <div>
                <nav>
                <ul>
                    <li>
                    <Link to="/users">Users</Link>
                    </li>
                </ul>
                </nav>

                <Switch>
                <Route path="/users">
                    <Users />
                </Route>
                </Switch>
            </div>
            </Router>
        )
    }

    render() {
        const { data } = this.state;
        const columns = [
            {
              title: "Parent ID",
              dataIndex: "id",
              defaultSortOrder: 'ascend',
              sorter: (a, b) => a.id - b.id,
            },
            {
              title: "Sender",
              dataIndex: "sender"
            },
            {
              title: "Receiver",
              dataIndex: "receiver"
            },
            {
              title: "Total Amount",
              dataIndex: "totalAmount"
            },
            {
              title: "Total Paid Amount",
              dataIndex: "totalPaidAmount",
              render: record => this.handleAction(record)
            },
        ];
        return (
            <div>
            <Table
                columns={columns}
                dataSource={data.parent}
                pagination={{ defaultPageSize: 2}}
                showHeader={true}
                rowKey={record => record.id}
            />
            </div>
        )
    }

}

export default TransactionDetails;
